let wordGiver = require('./wordGiver.js')
let responseGiver = require('./responseGiver.js')

async function numbersToWords(number){
	let model = {}
	try{
		model.status = 'success'
		model.number = typeof(number) == 'number' ? model.number = JSON.stringify(number) : model.number = number
		model = await wordGiver.pointRemover(model)
		model = await wordGiver.regex(model)
		model = await wordGiver.digitExtractor(model)
		model = await wordGiver.thoudandCrConverter(model)
		model = await wordGiver.hundredCrConverter(model)
		model = await wordGiver.tenCrConverter(model)
		model = await wordGiver.crConverter(model)
		model = await wordGiver.lacksConverter(model)
		model = await wordGiver.thousandConverter(model)
		model = await wordGiver.hundredConverter(model)
		model = await wordGiver.tensConverter(model)
		model = await wordGiver.unitConverter(model)
		model = await wordGiver.toFirstUpper(model)
		model = await wordGiver.replacer(model)
		model = await wordGiver.tagsDeleter(model)
		return (model)
	}
	catch(e){
		// console.log("------------catch------------")
		// console.log(model)
		model.status = 'failure'
		model.response = responseGiver[model.stage]
		model.NOTE = "Please email us on rohansolse@gmail.com for NPM related suggestions/bugs(with input)."
		model = await wordGiver.tagsDeleter(model)
		return model
	}
}

module.exports = numbersToWords